package wheel.component.demo;import android.app.Activity;import android.os.Bundle;import android.support.v4.app.FragmentActivity;import wheel.component.view.CircularTimer;public class CircularTimerActivity extends FragmentActivity{	private CircularTimer timer;	@Override	public void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		setContentView(R.layout.circular_timer_example);		timer = (CircularTimer) findViewById(R.id.circular_timer);		timer.setTimer(0, 0, 10);	}	}