package wheel.component.demo;import android.app.Activity;import android.content.Intent;import android.os.Bundle;import android.view.View;import android.widget.AdapterView;import android.widget.ArrayAdapter;import android.widget.ListView;import java.util.ArrayList;public class FunctionList extends Activity {	@Override	public void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		setContentView(R.layout.list_motion);		ArrayList<String> title = new ArrayList<String>();		title.add(WheelControllerActivity.class.getSimpleName());		title.add(WheelPopupControllerActivity.class.getSimpleName());		title.add(WheelDialogActivity.class.getSimpleName());		title.add(WheelPopupDialogActivity.class.getSimpleName());		title.add(WheelTwoDimensionPickerActivty.class.getSimpleName());		title.add(WheelTwoControllerActivity.class.getSimpleName());		title.add(WheelTwoPopupControllerActivity.class.getSimpleName());		title.add(WheelSinglePickerActivity.class.getSimpleName());		title.add(WheelFullDatePickerActivity.class.getSimpleName());		title.add(WheelTimerActivity.class.getSimpleName());		title.add(CircularTimerActivity.class.getSimpleName());		ArrayAdapter<String> adapter = new ArrayAdapter<String>(this, android.R.layout.simple_expandable_list_item_1, title);		ListView list = (ListView) findViewById(R.id.list);		list.setAdapter(adapter);		list.setOnItemClickListener(new ListView.OnItemClickListener() {			@Override			public void onItemClick(AdapterView<?> adapterView, View view, int position, long id) {				switch (position) {				case 0:					deliveryActivity(WheelControllerActivity.class);					break;				case 1:					deliveryActivity(WheelPopupControllerActivity.class);					break;				case 2:					deliveryActivity(WheelDialogActivity.class);					break;				case 3:					deliveryActivity(WheelPopupDialogActivity.class);					break;				case 4:					deliveryActivity(WheelTwoDimensionPickerActivty.class);					break;				case 5:					deliveryActivity(WheelTwoControllerActivity.class);					break;				case 6:					deliveryActivity(WheelTwoPopupControllerActivity.class);					break;				case 7:					deliveryActivity(WheelSinglePickerActivity.class);					break;				case 8:					deliveryActivity(WheelFullDatePickerActivity.class);					break;				case 9:					deliveryActivity(WheelTimerActivity.class);					break;				case 10:					deliveryActivity(CircularTimerActivity.class);					break;				}			}		});	}	private void deliveryActivity(Class<? extends Activity> inCalss) {		startActivity(new Intent(this, inCalss));	}}