package wheel.component.demo;import android.app.Activity;import android.content.Intent;import android.os.Bundle;import android.support.annotation.NonNull;import android.support.v4.app.FragmentActivity;import android.util.TypedValue;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.AdapterView;import android.widget.ArrayAdapter;import android.widget.ListView;import android.widget.TextView;import java.util.ArrayList;public class FunctionList extends FragmentActivity{	@Override	public void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		setContentView(R.layout.list_motion);		ArrayList<String> title = new ArrayList<String>();		title.add(WheelControllerActivity.class.getSimpleName());		title.add(WheelPopupControllerActivity.class.getSimpleName());		title.add(WheelDialogActivity.class.getSimpleName());		title.add(WheelPopupDialogActivity.class.getSimpleName());		title.add(WheelTwoDimensionPickerActivty.class.getSimpleName());		title.add(WheelTwoControllerActivity.class.getSimpleName());		title.add(WheelTwoPopupControllerActivity.class.getSimpleName());		title.add(WheelSinglePickerActivity.class.getSimpleName());		title.add(WheelFullDatePickerActivity.class.getSimpleName());		title.add(WheelTimerActivity.class.getSimpleName());		title.add(CircularTimerActivity.class.getSimpleName());		PlaceAutoCompleteAdapter adapter = new PlaceAutoCompleteAdapter(this, title);		ListView list = (ListView) findViewById(R.id.list);		list.setAdapter(adapter);		list.setOnItemClickListener(new ListView.OnItemClickListener() {			@Override			public void onItemClick(AdapterView<?> adapterView, View view, int position, long id) {				switch (position) {				case 0:					deliveryActivity(WheelControllerActivity.class);					break;				case 1:					deliveryActivity(WheelPopupControllerActivity.class);					break;				case 2:					deliveryActivity(WheelDialogActivity.class);					break;				case 3:					deliveryActivity(WheelPopupDialogActivity.class);					break;				case 4:					deliveryActivity(WheelTwoDimensionPickerActivty.class);					break;				case 5:					deliveryActivity(WheelTwoControllerActivity.class);					break;				case 6:					deliveryActivity(WheelTwoPopupControllerActivity.class);					break;				case 7:					deliveryActivity(WheelSinglePickerActivity.class);					break;				case 8:					deliveryActivity(WheelFullDatePickerActivity.class);					break;				case 9:					deliveryActivity(WheelTimerActivity.class);					break;				case 10:					deliveryActivity(CircularTimerActivity.class);					break;				}			}		});	}	private void deliveryActivity(Class<? extends Activity> inCalss) {		startActivity(new Intent(this, inCalss));	}	public class PlaceAutoCompleteAdapter extends ArrayAdapter<String>	{		private ArrayList<String> addresslist = new ArrayList<String>();		public PlaceAutoCompleteAdapter(@NonNull FragmentActivity act, ArrayList<String> addresslist)		{			super(act, R.layout.general_text, addresslist);			this.addresslist = addresslist;		}		@Override		public View getView(int position, View convertView, ViewGroup parent)		{			ViewHolder holder;			if (convertView != null)			{				holder = (ViewHolder) convertView.getTag();			}			else			{				convertView = LayoutInflater.from(FunctionList.this).inflate(R.layout.general_text, null);				holder = new ViewHolder();				holder.text = (TextView) convertView.findViewById(R.id.text);				convertView.setTag(holder);			}			holder.text.setTextSize(TypedValue.COMPLEX_UNIT_SP, 20);			holder.text.setMaxLines(1);			if (position < addresslist.size())			{				holder.text.setText(addresslist.get(position));			}			else			{				holder.text.setText("");			}			return convertView;		}		private class ViewHolder		{			public TextView text;		}	}}