package wheel.component.demo;import android.app.Activity;import android.os.Bundle;import android.util.Log;import android.widget.TextView;import java.util.Calendar;import wheel.component.datedialog.WheelPopUpFullDateDialog;import wheel.component.view.WheelFullDatePicker;import wheel.component.view.WheelFullDatePicker.WheelFullDateSyncListener;public class WheelFullDatePickerActivity extends Activity {	private WheelFullDatePicker date_picker;	private TextView title;	@Override	protected void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		setContentView(R.layout.full_date_wheel_picker);		title = (TextView) findViewById(R.id.title);		date_picker = (WheelFullDatePicker) findViewById(R.id.date_picker);		date_picker.setShowTaiwanYear(true);		date_picker.setCanSetUpFutureTime(true);		date_picker.setCanSetUpPastTime(true);		date_picker.setSetCurrentTimeVisible(true);		date_picker.setCanRestrictBetweenDate(true);		try {			date_picker.setRestrictDate(beforeDate(), afterDate());			date_picker.scrollToCertainDate(Calendar.getInstance());		} catch (Exception e) {			e.printStackTrace();		}		date_picker.setWheelListener(title.getId(), new WheelFullDateSyncListener() {			@Override			public void handleDate(int viewID, Calendar calendar) {				title.setText(WheelPopUpFullDateDialog.showCalendarText(calendar));			}		});	}	/** 上2個月月初 **/	public Calendar beforeDate() {		Calendar want = Calendar.getInstance();		want.set(want.get(Calendar.YEAR) - 80, 5, 1);		Log.d("josephWang", "beforeDate " + WheelPopUpFullDateDialog.showCalendarText(want));		return want;	}	/** 下2個月月底 **/	public Calendar afterDate() {		Calendar want = Calendar.getInstance();		want.set(want.get(Calendar.YEAR) + 10, 9, 31);		Log.d("josephWang", "afterDate " + WheelPopUpFullDateDialog.showCalendarText(want));		return want;	}}