package wheel.component.demo;import android.app.Activity;import android.os.Bundle;import android.util.Log;import android.view.View;import android.widget.TextView;import wheel.component.view.WheelTimer;import wheel.component.view.WheelTimer.TimeData;import wheel.component.view.WheelTimer.WheelTimerSelectListener;public class WheelTimerActivity extends Activity {	private WheelTimer timer;	private TextView test;	@Override	public void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		setContentView(R.layout.test_wheel_timer);		test = (TextView) findViewById(R.id.test);		timer = (WheelTimer) findViewById(R.id.timer);		timer.setTimer(1, 1, 59);		timer.setWheeSelectlListener(wheelListener);		findViewById(R.id.pause).setOnClickListener(listener);		findViewById(R.id.stop).setOnClickListener(listener);		findViewById(R.id.resume).setOnClickListener(listener);	}	private View.OnClickListener listener = new View.OnClickListener() {		@Override		public void onClick(View v) {			if (v.getId() == R.id.pause) {				timer.pauseTimer();			} else if (v.getId() == R.id.stop) {				timer.stopTimer();			} else if (v.getId() == R.id.resume) {				timer.resumeTimer();			}		}	};	private WheelTimerSelectListener wheelListener = new WheelTimerSelectListener() {		@Override		public void handleTime(TimeData timeData) {			test.setText("" + timeData.hourText + ":" + timeData.minuteText + ":" + timeData.secondText);			Log.d("josephWang", "TimeData hour " + timeData.hour);			Log.d("josephWang", "TimeData minute  " + timeData.minute);			Log.d("josephWang", "TimeData second " + timeData.second);			Log.d("josephWang", "TimeData hourText " + timeData.hourText);			Log.d("josephWang", "TimeData minuteText " + timeData.minuteText);			Log.d("josephWang", "TimeData secondText " + timeData.secondText);		}	};}