package wheel.component.view;import android.os.Handler;import android.os.Message;public class CircularTimerHandler {	/**	 * Receiving the procedure of "Circular" listener interface.	 */	public static interface CircularListener {		public void onCircular(CountDownTimeMode mode, long tickedTime);	}	private CircularListener listener;	public CircularTimerHandler(CircularListener listener) {		this.listener = listener;	}	private Message message = Message.obtain();	public void handleProcedure(CountDownTimeMode countDownTimeMode, long tickedTime) {		message.what = countDownTimeMode.ordinal();		message.obj = tickedTime;		timeHandler.handleMessage(message);	}	private Handler timeHandler = new Handler() {		public void handleMessage(Message msg) {			listener.onCircular(CountDownTimeMode.getStatus(msg.what), Long.parseLong(msg.obj.toString()));		}	};}